import { createContext, useContext, useReducer, useState } from "react";

const favoritos = JSON.parse(localStorage.getItem("favorito"))
const temaGuardo = JSON.parse(localStorage.getItem)

export const initialState = {theme: "light", data: favoritos ? favoritos : []}

export const reducer = (state, action) => {
  switch (action.type) {
    case "ADD_FAV":
      return {...state, data: [...state.data, action.payload]}
    case "CHANGE_THEME":
      return {...state, theme: state.theme == "light" ? "dark" : "light"}   
    default:
      throw new Error()
  }
}

export const ContextGlobal = createContext(undefined);


export const Context = ({ children }) => {
  //Aqui deberan implementar la logica propia del Context, utilizando el hook useMemo

  const [state, dispatch] = useReducer(reducer, initialState)

  const [tema, setTema] = useState(state.theme == "light")

  const changeTheme = () =>{
    dispatch({type: "CHANGE_THEME"})
    setTema(!tema)
  }

  return (
    <ContextGlobal.Provider value={{
      state, dispatch, changeTheme, tema
    }}>
      {children}
    </ContextGlobal.Provider>
  );
};


export const useContextGlobal = () => useContext(ContextGlobal)

export default Context;


