export function prepareStartHandler(
  handler: StartHandler,
  context: SetupWorkerInternalContext,
): SetupWorkerApi['start'] {
  return (initialOptions) => {
    context.startOptions = resolveStartOptions(initialOptions)
    return handler(context.startOptions, initialOptions || {})
  }
}
import {
  Interceptor,
  BatchInterceptor,
  HttpRequestEventMap,
} from '@mswjs/interceptors'
import { FetchInterceptor } from '@mswjs/interceptors/lib/interceptors/fetch'
import { XMLHttpRequestInterceptor } from '@mswjs/interceptors/lib/interceptors/XMLHttpRequest'
import {
  SerializedResponse,
  SetupWorkerInternalContext,
  StartOptions,
} from '../glossary'
import type { RequiredDeep } from '../../typeUtils'
import { handleRequest } from '../../utils/handleRequest'
import { MockedRequest } from '../../utils/request/MockedRequest'
import { serializeResponse } from '../../utils/logging/serializeResponse'
import { createResponseFromIsomorphicResponse } from '../../utils/request/createResponseFromIsomorphicResponse'

export function createFallbackRequestListener(
  context: SetupWorkerInternalContext,
  options: RequiredDeep<StartOptions>,
): Interceptor<HttpRequestEventMap> {
  const interceptor = new BatchInterceptor({
    name: 'fallback',
    interceptors: [new FetchInterceptor(), new XMLHttpRequestInterceptor()],
  })

  interceptor.on('request', async (request) => {
    const mockedRequest = new MockedRequest(request.url, {
      ...request,
      body: await request.arrayBuffer(),
    })

    const response = await handleRequest<SerializedResponse>(
      mockedRequest,
      context.requestHandlers,
      options,
      context.emitter,
      {
        transformResponse(response) {
          return {
            status: response.status,
            statusText: response.statusText,
            headers: response.headers.all(),
            body: response.body,
            delay: response.delay,
          }
        },
        onMockedResponse(_, { handler, publicRequest, parsedRequest }) {
          if (!options.quiet) {
            context.emitter.once('response:mocked', (response) => {
              handler.log(
                publicRequest,
                serializeResponse(response),
                parsedRequest,
              )
            })
          }
        },
      },
    )

    if (response) {
      request.respondWith(response)
    }
  })

  interceptor.on('response', (request, response) => {
    if (!request.id) {
      return
    }

    const browserResponse = createResponseFromIsomorphicResponse(response)

    if (response.headers.get('x-powered-by') === 'msw') {
      context.emitter.emit('response:mocked', browserResponse, request.id)
    } else {
      context.emitter.emit('response:bypass', browserResponse, request.id)
    }
  })

  interceptor.apply()

  return interceptor
}
import { encodeBuffer, IsomorphicResponse } from '@mswjs/interceptors'

const noop = () => {
  throw new Error('Not implemented')
}

export function createResponseFromIsomorphicResponse(
  response: IsomorphicResponse,
): Response {
  return {
    ...response,
    ok: response.status >= 200 && response.status < 300,
    url: '',
    type: 'default',
    status: response.status,
    statusText: response.statusText,
    headers: response.headers,
    body: new ReadableStream(),
    redirected: response.headers.get('Location') != null,
    async text() {
      return response.body || ''
    },
    async json() {
      return JSON.parse(response.body || '')
    },
    async arrayBuffer() {
      return encodeBuffer(response.body || '')
    },
    bodyUsed: false,
    formData: noop,
    blob: noop,
    clone: noop,
  }
}
import { createFallbackRequestListener } from './createFallbackRequestListener'
import { SetupWorkerInternalContext, StartHandler } from '../glossary'
import { printStartMessage } from './utils/printStartMessage'

export function createFallbackStart(
  context: SetupWorkerInternalContext,
): StartHandler {
  return async function start(options) {
    context.fallbackInterceptor = createFallbackRequestListener(
      context,
      options,
    )

    printStartMessage({
      message: 'Mocking enabled (fallback mode).',
      quiet: options.quiet,
    })

    return undefined
  }
}
import { SetupWorkerInternalContext, StopHandler } from '../glossary'
import { printStopMessage } from './utils/printStopMessage'

export function createFallbackStop(
  context: SetupWorkerInternalContext,
): StopHandler {
  return function stop() {
    context.fallbackInterceptor?.dispose()
    printStopMessage({ quiet: context.startOptions?.quiet })
  }
}
import { EventEmitter } from 'stream'

/**
 * Pipes all emitted events from one emitter to another.
 */
export function pipeEvents(
  source: EventEmitter,
  destination: EventEmitter,
): void {
  const rawEmit = source.emit

  // @ts-ignore
  if (rawEmit._isPiped) {
    return
  }

  source.emit = function (event, ...data) {
    destination.emit(event, ...data)
    return rawEmit.call(this, event, ...data)
  }

  // @ts-ignore
  source.emit._isPiped = true
}
/**
 * Creates an immutable copy of the given array.
 */
export function toReadonlyArray<T>(source: Array<T>): ReadonlyArray<T> {
  const clone = [...source] as Array<T>
  Object.freeze(clone)
  return clone
}
import { DefaultBodyType, ResponseResolver } from './handlers/RequestHandler'
import {
  RESTMethods,
  RestContext,
  RestHandler,
  RestRequest,
} from './handlers/RestHandler'
import { Path, PathParams } from './utils/matching/matchRequestUrl'

function createRestHandler<Method extends RESTMethods | RegExp>(
  method: Method,
) {
  return <
    RequestBodyType extends DefaultBodyType = DefaultBodyType,
    Params extends PathParams<keyof Params> = PathParams,
    ResponseBody extends DefaultBodyType = DefaultBodyType,
  >(
    path: Path,
    resolver: ResponseResolver<
      RestRequest<
        Method extends RESTMethods.HEAD | RESTMethods.GET
          ? never
          : RequestBodyType,
        Params
      >,
      RestContext,
      ResponseBody
    >,
  ) => {
    return new RestHandler(method, path, resolver)
  }
}

export const rest = {
  all: createRestHandler(/.+/),
  head: createRestHandler(RESTMethods.HEAD),
  get: createRestHandler(RESTMethods.GET),
  post: createRestHandler(RESTMethods.POST),
  put: createRestHandler(RESTMethods.PUT),
  delete: createRestHandler(RESTMethods.DELETE),
  patch: createRestHandler(RESTMethods.PATCH),
  options: createRestHandler(RESTMethods.OPTIONS),
}
import type { DocumentNode, OperationTypeNode } from 'graphql'
import { ResponseResolver } from './handlers/RequestHandler'
import {
  GraphQLHandler,
  GraphQLContext,
  GraphQLRequest,
  GraphQLVariables,
  ExpectedOperationTypeNode,
  GraphQLHandlerNameSelector,
} from './handlers/GraphQLHandler'
import { Path } from './utils/matching/matchRequestUrl'

export interface TypedDocumentNode<
  Result = { [key: string]: any },
  Variables = { [key: string]: any },
> extends DocumentNode {
  __apiType?: (variables: Variables) => Result
  __resultType?: Result
  __variablesType?: Variables
}

function createScopedGraphQLHandler(
  operationType: ExpectedOperationTypeNode,
  url: Path,
) {
  return <
    Query extends Record<string, any>,
    Variables extends GraphQLVariables = GraphQLVariables,
  >(
    operationName:
      | GraphQLHandlerNameSelector
      | DocumentNode
      | TypedDocumentNode<Query, Variables>,
    resolver: ResponseResolver<
      GraphQLRequest<Variables>,
      GraphQLContext<Query>
    >,
  ) => {
    return new GraphQLHandler<GraphQLRequest<Variables>>(
      operationType,
      operationName,
      url,
      resolver,
    )
  }
}

function createGraphQLOperationHandler(url: Path) {
  return <
    Query extends Record<string, any>,
    Variables extends GraphQLVariables = GraphQLVariables,
  >(
    resolver: ResponseResolver<
      GraphQLRequest<Variables>,
      GraphQLContext<Query>
    >,
  ) => {
    return new GraphQLHandler<GraphQLRequest<Variables>>(
      'all',
      new RegExp('.*'),
      url,
      resolver,
    )
  }
}

const standardGraphQLHandlers = {
  /**
   * Captures any GraphQL operation, regardless of its name, under the current scope.
   * @example
   * graphql.operation((req, res, ctx) => {
   *   return res(ctx.data({ name: 'John' }))
   * })
   * @see {@link https://mswjs.io/docs/api/graphql/operation `graphql.operation()`}
   */
  operation: createGraphQLOperationHandler('*'),

  /**
   * Captures a GraphQL query by a given name.
   * @example
   * graphql.query('GetUser', (req, res, ctx) => {
   *   return res(ctx.data({ user: { name: 'John' } }))
   * })
   * @see {@link https://mswjs.io/docs/api/graphql/query `graphql.query()`}
   */
  query: createScopedGraphQLHandler('query' as OperationTypeNode, '*'),

  /**
   * Captures a GraphQL mutation by a given name.
   * @example
   * graphql.mutation('SavePost', (req, res, ctx) => {
   *   return res(ctx.data({ post: { id: 'abc-123' } }))
   * })
   * @see {@link https://mswjs.io/docs/api/graphql/mutation `graphql.mutation()`}
   */
  mutation: createScopedGraphQLHandler('mutation' as OperationTypeNode, '*'),
}

function createGraphQLLink(url: Path): typeof standardGraphQLHandlers {
  return {
    operation: createGraphQLOperationHandler(url),
    query: createScopedGraphQLHandler('query' as OperationTypeNode, url),
    mutation: createScopedGraphQLHandler('mutation' as OperationTypeNode, url),
  }
}

export const graphql = {
  ...standardGraphQLHandlers,
  link: createGraphQLLink,
}
