import React, { useState } from "react";


const Form = () => {
  //Aqui deberan implementar el form completo con sus validaciones

  const [nombre, setNombre] = useState("")
  const [email, setEmail] = useState("")
  const [show, setShow] = useState(false)
  const [error, setError] = useState(false)


  const handleSubmit = (e) => {

    e.preventDefault()

    if (nombre.length > 5 && email.includes("@") ) {
      setShow(true)
      setError(false)
    } else{
      setError(true)
      setShow(false)
    }

  }


  return (

    <div>

      <form onSubmit={handleSubmit}>

        <label> Ingresa tu nombre completo </label>
        <input type="text" onChange={ (e) => setNombre(e.target.value)} />

        <label> Ingresa tu Email </label>
        <input type="text" onChange={ (e) => setEmail(e.target.value)} />

        <button> Enviar </button>

      </form>

      {error && <p style={{color:"red"}}>¡El nombre completo es muy corto, mínimo 5 caracteres!</p>}
      {show && <p style={{color:"green"}}>Información enviada con éxito!</p>}

    </div>

  );
};

export default Form;
